mapper.select:
SELECT BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,TOTAL_CNT ,CREATOR FROM ( SELECT BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,ROW_NUMBER() OVER(ORDER BY BOARD_NUM DESC) AS NUMROW ,COUNT(*) OVER() AS TOTAL_CNT ,CREATOR FROM BOARD
WHERE BOARD_TYPE = #{boardType}
) WHERE NUMROW BETWEEN (#{pageNo}-1)* 5 + 1 AND (#{pageNo})*5

mapper.insert:
INSERT INTO
BOARD
(BOARD_TYPE
,BOARD_NUM
,BOARD_TITLE
,BOARD_COMMENT
,CREATOR
,CREATE_TIME
,MODIFIER
,MODIFIED_TIME
)
VALUES
(#{boardType}
,(SELECT NVL(TO_NUMBER(MAX(BOARD_NUM)+1),1)
FROM BOARD
)
,#{boardTitle}
,#{boardComment}
,#{creator}
,TO_CHAR(SYSDATE)
,'SYSTEM'
,TO_CHAR(SYSDATE)
)

mapper.update:
UPDATE
BOARD
SET BOARD_TITLE = #{boardTitle}
,
BOARD_COMMENT = #{boardComment}
, MODIFIED_TIME = TO_CHAR(SYSDATE)
WHERE BOARD_NUM = #{boardNum}
AND BOARD_TYPE = #{boardType}

mapper.delete:
DELETE
FROM BOARD
WHERE BOARD_NUM = #{boardNum}
AND
BOARD_TYPE = #{boardType}
